import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector} from 'react-redux';
import { listProducts, saveProduct } from '../actions/productActions';

function ProductsScreen(props) {
    const [modalVisible, setModalVisible] = useState(false);
    const [name, setName] = useState('');
    const [id, setId] = useState('');
    const [price, setPrice] = useState('');
    const [image, setImage] = useState('');
    const [brand, setBrand] = useState('');
    const [category, setCategory] = useState('');
    const [countInStock, setCountInStock] = useState('');
    const [description, setDescription] = useState('');
    const productList = useSelector(state => state.productList);
    const {loading, products, error} = productList;
    const productSave = useSelector(state => state.productSave);
    const {loading: loadingSave, success: successSave, error: errorSave } = productSave;
    const dispatch = useDispatch();

    useEffect(() => {
       dispatch(listProducts());
        return () => {
            //;
        };
    }, []);

    const openModal = (product) => {
        setModalVisible(true);
        setId(product._id);
        setName(product.name);
        setPrice(product.price);
        setCountInStock(product.countInStock);
        setCategory(product.category);
        setImage(product.image);
        setBrand(product.brand);
        setDescription(product.description);
    };

    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(saveProduct({name, price, image, brand, 
            category, countInStock, description}));
    }


    return <div className='content content-margined'>
        <div className='product-header'>
            <h3>Products</h3>
            <button className='button primary' onclick={() => openModal({})}> Create Product</button>
        </div>
        <div className='product-list'>
            <table>
                <thread>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Price</th>
                        <th>Category</th>
                        <th>Brand</th>
                        <th>Action</th>
                    </tr>
                </thread>
                <tbody>
                    {products.map(product => 
                        (<tr>
                        <td>{productList._id}</td>
                        <td>{productList.name}</td>
                        <td>{productList.price}</td>
                        <td>{productList.category}</td>
                        <td>{productList.brand}</td>
                        <td>
                            <button onClick={() => openModal(product)}>Edit</button>
                            <button>Delete</button>
                        </td>
                    </tr>))} 
                </tbody>
            </table>
        </div>
        {modalVisible && <div className="form">
            <form onSubmit={submitHandler}>
                <ul className="form-container">
                    <li>
                        <h3>Create Product</h3>
                    </li>
                    <li>
                        {loadingSave && <div>Loading...</div>}
                        {errorSave && <div>{errorSave}</div>}
                    </li>
                    <li>
                        <label htmlFor="name">
                            Name
                        </label>
                        <input
                        type="text"
                        name="name"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}>
                        </input>
                    </li>
                    <li>
                    <label htmlFor="price">Price</label>
                    <input
                    type="text"
                    name="price"
                    value={price}
                    id="price"
                    onChange={(e) => setPrice(e.target.value)}
                    ></input>
                </li>
                <li>
                    <label htmlFor="image">Image</label>
                    <input
                    type="text"
                    name="image"
                    value={image}
                    id="image"
                    onChange={(e) => setImage(e.target.value)}
                    ></input>
                    <input type="file" onChange={uploadFileHandler}></input>
                    {uploading && <div>Uploading...</div>}
                </li>
                <li>
                    <label htmlFor="brand">Brand</label>
                    <input
                    type="text"
                    name="brand"
                    value={brand}
                    id="brand"
                    onChange={(e) => setBrand(e.target.value)}
                    ></input>
                </li>
                <li>
                    <label htmlFor="countInStock">CountInStock</label>
                    <input
                    type="text"
                    name="countInStock"
                    value={countInStock}
                    id="countInStock"
                    onChange={(e) => setCountInStock(e.target.value)}
                    ></input>
                </li>
                <li>
                    <label htmlFor="category">Category</label>
                    <input
                    type="text"
                    name="category"
                    value={category}
                    id="category"
                    onChange={(e) => setCategory(e.target.value)}
                    ></input>
                </li>
                <li>
                    <label htmlFor="description">Description</label>
                    <textarea
                    name="description"
                    value={description}
                    id="description"
                    onChange={(e) => setDescription(e.target.value)}
                    ></textarea>
                </li>
                    <li>
                        <button type="submit" className="button primary"> Create</button>
                    </li>
                    <li>
                        <button type="submit" className="button primary"> Create</button>
                    </li>
                </ul>
            </form>
        </div>}
    </div>
}

export default ProductsScreen;
